function handleButtons(player)
	if btn(0) then player.y=player.y-2 end
	if btn(1) then player.y=player.y+2 end
	if btn(2) then player.x=player.x-2 end
	if btn(3) then player.x=player.x+2 end
	
	if btnp(4) then
		player.weapon=player.weapon+1
	end
end

function updateProjectiles(projectiles)
	for key, projectile in pairs(projectiles) do
		if projectile.type%2==0 then
			speed=4
			
			if projectile.id==1 then
				projectile.y=projectile.y+speed
			elseif projectile.id==2 then
				projectile.x=projectile.x+speed
			elseif projectile.id==3 then
				projectile.y=projectile.y-speed
			elseif projectile.id==4 then
				projectile.x=projectile.x-speed
			end

		else
			speed=3
			
			if projectile.id==1 then
				projectile.x=projectile.x+speed
				projectile.y=projectile.y+speed
			elseif projectile.id==2 then
				projectile.x=projectile.x+speed
				projectile.y=projectile.y-speed
			elseif projectile.id==3 then
				projectile.x=projectile.x-speed
				projectile.y=projectile.y-speed
			elseif projectile.id==4 then
				projectile.x=projectile.x-speed
				projectile.y=projectile.y+speed
			end
		
		end
		
		if projectile.x<0 or projectile.x>239
				or projectile.y<0 or projectile.y>135
				then
			table.remove(projectiles,key)
		end
	end
end

function collide(a,b)
	dist=math.pow(a.x-b.x,2)+math.pow(a.y-b.y,2)
	r=math.pow(a.r+b.r,2)
	
	return(dist<=r)
end

function updateTarget(target)
	if target.x<-15 then
	 score=score+50
		target.x=255
		target.y=math.random(136)
		target.ty=target.y
		target.s=math.random(0,4)
		target.rand=math.random(0,360)
	end
	
	target.x=target.x-1
	if t%1==0 then
		target.y=target.ty+30*math.sin((target.x+target.rand)*1*(math.pi/180))
	end
end

