t=0
player={x=120,y=63,r=4,alive=true,weapon=0}
target={x=200,y=100,ty=100,r=4,s=0,rand=0,alive=true}
projectiles={}
killSreen=false
score=0


function TIC()
	if t < 400 then
		cls(15)
		print("You Are Orb",90,math.min(63,t/4),12)
	elseif player.alive then
		handleButtons(player)
		
		if t%60==0 then
			for i=1,4 do
				p={id=i,type=player.weapon,x=player.x,y=player.y,r=1}
				table.insert(projectiles,p)
			end
		end
		
		updateProjectiles(projectiles)
		updateTarget(target)
	
		cls(0)
		spr(256+target.s,
		    target.x-4,
						target.y-4,
						0)
		--circ(target.x,target.y,target.r,9)
		for id, projectile in pairs(projectiles) do
			rect(projectile.x-1,projectile.y-1,2,2,4)
			if collide(projectile,target) then
				player.alive=false
			end
		end
		spr(261,player.x-8,player.y-8,0,1,0,0,2,2)
		--circ(player.x,player.y,player.r,2)
		print(string.format("%06d",score),202,4,4)
	elseif not killScreen then
	 print("Don't Shoot Orbs",78,63,12)
		killScreen=true
	end

	t=t+1
end

